BEGIN
-- Create a temp table to store the select results CREATE TABLE #PageIndex ( [IndexId] int IDENTITY (1, 1) NOT NULL, [CourseGroupId] int )
-- Insert into the temp table INSERT INTO #PageIndex ( [CourseGroupId] ) SELECT TOP {3} [CourseGroupId] FROM [dbo].[CourseGroup] {0} ORDER BY {1}
-- Return paged results SELECT O.[CourseGroupId], O.[Name], O.[Note] FROM [dbo].[CourseGroup] O, #PageIndex PageIndex WHERE PageIndex.IndexId > {2} AND O.[CourseGroupId] = PageIndex.[CourseGroupId] ORDER BY PageIndex.IndexId
